INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (371, 'Povinné roušky v metru a na nádražích', 'Povinnost nošení roušek platí pro metro a jeho prostory a ve vnitřních prostech nádraží', '- Jste dítě do dvou let
