INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (372, 'Návštěvy max. 2 lidi', 'Návštěvy jsou povoleny v počtu nejvýše dvou osob u jednoho pacienta ve stejný čas, každá osoba navštěvující pacienta je dotazována na symptomy onemocnění covid-19, např. formou dotazníku, pokud navštěvující osoba vykazuje pozitivní příznak odpovídající nemoci, nelze připustit její přítomnost u pacienta, pokud je to možné, návštěvy probíhají ve venkovních prostorách zařízení', '<ul><li>Uvedená omezení se nevztahují na návštěvy pacientů v terminálním stádiu</li></ul>', 'narizeni', 'modal-md', 'fas fa-exclamation-triangle', 1, null, 22);
INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (373, 'Návštěvy max. 2 lidi', 'Návštěvy jsou povoleny v počtu nejvýše dvou osob u jednoho pacienta ve stejný čas, každá osoba navštěvující pacienta je dotazována na symptomy onemocnění covid-19, např. formou dotazníku, pokud navštěvující osoba vykazuje pozitivní příznak odpovídající nemoci, nelze připustit její přítomnost u pacienta, pokud je to možné, návštěvy probíhají ve venkovních prostorách zařízení', '<ul><li>Uvedená omezení se nevztahují na návštěvy pacientů v terminálním stádiu</li></ul>', 'narizeni', 'modal-md', 'fas fa-exclamation-triangle', 10, null, 22);
INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (374, 'Minimální délka izolace je 10 dní', 'Všem pozitivně testovaným se nařídí izolace v minimální délce 10 dní ode dne odběru vzorku. Izolace končí po uplynutí 10 dnů u bezpříznakových osob, popřípadě po 3 dnech po odeznění příznaků. Délka izolace však nesmí být kratší než 10 dnů. Izolace se tedy nemusí ukončovat dalším PCR testem.', 'U zdravotníků se izolace ukončuje až po negativním výsledku PCR testu.', 'narizeni', 'modal-md', 'fas fa-exclamation-triangle', 12, null, 12);
INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (375, 'Minimální délka karantény je 10 dní', 'Všem osobám označeným za blízké kontakty nakaženého, se nařídí karanténa v minimální délce 10 dní ode dne posledního kontaktu s nakaženým. Za tuto dobu bude osoba v karanténě dvakrát testována - nejdříve v rozmezí 1-5 dní od posledního kontaktu, poté ještě po uplynutí 10 dní. Pokud osoba nebude vykazovat příznaky a oba testy budou negativní, bude mu po 10 dnech karanténa ukončena. Pokud bude vykazovat příznaky nebo bude mít pozitivní test, bude mu nařízena izolace.', null, 'narizeni', 'modal-md', 'fas fa-exclamation-triangle', 12, null, 12);
