INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (399, 'Roušky ve všech vnitřních prostorech mimo bydliště', 'Povinnost nosit roušku ve všech vnitřních prostorech s výjimkami. Pro školy vyhrazen separátní bod. ', '
<ul>
