INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (401, 'Roušky ve školách', 'Povinnost nosit roušku ve všech společných vnitřních prostorech. Nově pro 2. stupeň ZŠ a výše (SŠ, VŠ, VOŠ atd) platí povinnost roušek i ve třídách/učebnách. Nižší stupeň ZŠ roušky nosit nemusí ve třídách, musí ve společných prostorech (chodby, záchody...)', 'Povinnost nošení roušky se nevztahuje se na: 
<h5>Nižší stupeň (1. - 5. třída), MŠ, jesle...</h5>
