INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (409, 'Zákaz návštěv v nemocnicích; podmínky pro přítomnost u porodu', 'Zákaz návštěv ve zdravotnických zařízeních lůžkové péče', '<ul>
