INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (419, 'Omezení návštěv třetích osob u pacientů', 'Poskytovatelům zdravotních služeb bylo vydáno doporučeho omezit návštěvy v zdrav. zařízení lůžkové péče. Jestli se tím budou řídit je už na dané nemocnici.', 'Výjimkami z doporučení jsou: <ul>
	<li>Návštěv nezletilých pacientů</li>
	<li>Návštěv pacientů s omezenou svéprávností</li
	<li>Návštěv pacientů v hospicech a dalších pacientů v terminálním stádiu nevyléčitelného onemocnění</li>
	<li>Přítomnost pří porodu, za předpokladu, že jsou splněny následující podmínky</li>
	<ul>
		<li>Jste druhým rodičem dítěte, nebo žijete s rodičkou ve společné domácnosti</li>
		<li>Porod bude probíhat v damostatném porodním pokoji nebo bodu s vlastním soc. zařízením</li>
		<li>Bude omezen váš kontakt s ostatními rodičkami</li>
		<li>Nemáte teplotu přesahující 37.0°C, ani příznaky COVID-19. Kontrole se podrobíte před vstupem do porodního pokoje</li>
		<li>Máte chirurgickou roušku, nebo lepší ochranu dýchacích cest (respirátor). Po domácku ušitá rouška nestačí. </li>
	</ul>
</ul>', 'doporuceni', 'modal-md', 'fas fa-exclamation-triangle', 1, null, 68);
INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (420, 'Omezení návštěv v soc. zařízeních (např. domovy seniorů)', 'Doporučení pro poskytovatele soc. služeb omezit návštěvy v soc. zařízeních pobytové péče, jako jsou například 
<ul>
	<li>domovy pro osoby se zdravotním postižením</li>
	<li>domovy pro seniory</li>
	<li>domovy se zvláštním režimem</li>
	<li>chráněné bydlení</li>
	<li>azylové domy</li>
	<li>domy na půl cesty<li>
	<li>intervenční centra</li>
</ul> a všem odlehčovacím sociálním službám v pobytové formě', 'Doporučenými výjimkami jsou (dané nařízení se tímto nemusí nutně řídit)
<ul>
	<li>Návštěv nezletilých uživatelů</li>
	<li>Návštěv uživatelů s omezenou svéprávností</li
	<li>Návštěv uživatelů v terminálním stádiu nevyléčitelného onemocnění</li>
</ul>', 'doporuceni', 'modal-md', 'fas fa-exclamation-triangle', 10, null, 68);
