INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (434, 'Zákaz návštěv pacientů', 'Zázka návštěv ve zdravotnických zařízeních lůžkové péče. ', 'Výjimkou jsou: <ul>
