INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (524, 'ZŠ: Do školy chodí pouze polovina tříd, po týdnu se střídají', 'Zákaz osobní přítomnosti žáků poloviny tříd na základních školách. V týdnu od 12. do 16. října 2020 se prezenčně neúčastní první polovina tříd (učí se tedy distančně) a v týdnu od 19. do 23. října 2020 se neúčastní druhá polovina tříd. Třídy jako takové se nedělí.', 'Výjimku mají žáci se speciálními vzdělávacími potřebami (podle § 16 odst. 9 školského zákona).', 'narizeninouzovy', 'modal-md', 'fas fa-exclamation-triangle', 4, null, 109);
INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (527, 'Zákaz návštěv v soc. zařízeních (např. v domovech pro seniory)', 'Zákaz návštěv uživatelů v domovech pro seniory, domovech se zvláštním režimem a všech odlehčovacích sociálních službách.', 'Jsou povoleny pouze návštěvy:
