INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (531, 'Účast na svatbě, vstupu do registrovaného partnerství, pohřbu a smuteční hostině', 'Omezuje se : <ul>

	<li>účast na svatbě, prohlášení osob o tom, že spolu vstupují do registrovaného partnerství, pohřbu a následné oslavě, resp. smuteční hostině, konaných nejpozději následujícího dne</li>
	<li>účast na každé z těchto akcí není vyšší než 30 osob</li>
	<li>pro účel oslavy podle tohoto bodu se zakazuje přítomnost veřejnosti v provozovnách stravovacích služeb pouze v čase mezi 00:00 hod. a 06:00 hod.</li>
</ul>', null, 'narizeninouzovy', 'modal-md', 'fas fa-birthday-cake', 13, null, 116);
INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (532, 'Roušky všude na veřejnosti', 'Zakazuje se pohyb a pobyt bez ochranných prostředků dýchacích cest (úst i nosu!) proti šíření kapének (rouška, respirátor, šátek,...) ve všech prostorech mimo bydliště nebo místo ubytování (např. hotelový pokoj), s výjimkami viz níže.', 'Roušku venku mít nemusíte, pokud se nacházíte na veřejnosti nepřístupném místě nebo pokud jste si jisti, že mezi vámi a ostatními osobami zůstane zachován 2 metrový odstup.<br>
<br>
Roušku tedy nemusíte mít třeba na pozemku vaší chaty nebo na procházce v lese (pokud vás na cestě nemíjejí další osoby).<br>
<br>
Tento zákaz samozřejmě toleruje kontakt členů jedné domácnosti.<br>
<br>
Tento zákaz neplatí pro:
<ul>
   <li>děti do 2 let</li>
   <li>děti a pracovníky či pečující osoby v MŠ nebo dětské skupině</li>
   <li>ubytované a zaměstnance v internátech a domovech mládeže</li>
   <li>zaměstnance a děti v zařízeních pro děti vyžadující okamžitou pomoc</li>
   <li>osoby s poruchou intelektu a dalšími poruchami, jejichž stav dodržování neumožňuje</li>
   <li>hospitalizoavné pacienty v zařízeních lůžkové péče a po nezbytně nutnou dobu i pro zdravotnické pracovníky při poskytování zdravotních služeb</li>
   <li>uživatele sociálních služeb (tj. stacionáře, domovy pro seniory nebo zdravotně postižené, domovy se zvláštním režimem a zařízení poskytující odlehčovací soc. služby v pobytové formě)</li>
   <li>další případy hodné zřetele podle poskytovatele zdrav. či soc. služeb nebo podle lékaře</li>
   <li>zaměstnance, když vykonávají práci na jednom místě, ale s 2metrovými rozestupy</li>
   <li>osoby, které vykonávají práci ve třetí nebo čtvrté kategorii pro rizikové podmínky</li>
   <li>většinu účastníků soudního řízení</li>
   <li>přednášející, účinkující, umělce vykonávající dílo, moderátory a redaktory</li>
