INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (533, 'Roušky ve veřejné dopravě', 'Zakazuje se pohyb a pobyt bez ochranných prostředků dýchacích cest (úst i nosu!) proti šíření kapének (rouška, respirátor, šátek,...) v prostředcích veřejné dopravy, s výjimkami viz níže.', 'Tento zákaz neplatí pro:
<ul>
