INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (551, 'Zákaz pohybu osob kromě nezbytného (lockdown)', 'Zakazuje se zbytečný pohyb osob, tzn. všechno kromě následujících výjimek...', 'Povoleny jsou pouze:

