INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (552, 'Omezení kontaktů a pobytu na veřejnosti', 'Nařizuje se omezit kontakt s jinými osobami na nezbytně nutnou míru. Pohyb na veřejně přístupných místech se proto omezuje na dobu nezbytně nutnou a nařizuje se pobývat v místě bydliště. Pobývat na veřejnosti se smí maximálně v počtu 2 osob a při pohybu se zachovává odstup od ostatních na alespoň 2 metry, pokud je to možné.', 'Výjimku z maximálního počtu 2 osob mají:
