INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (579, 'Zákaz vycházení v noci', 'Zakazuje se volný pohyb osob večer a v noci (mezi 21:00 a 05:00). Výjimek je mnohem méně než přes den...', 'Večer a v noci je povoleno vycházet pouze v těchto důvodech:

