INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (593, 'Distanční výuka na všech školách kromě MŠ (s výjimkami)', 'Zakazuje se osobní přítomnost studentů na výuce na vysokých školách (a účastníků na kurzech celoživotního vzdělávání), konzervatořích, vyšších odborných, středních, základních, uměleckých i jazykových školách. Na jazykových školách je zakázána také účast na státní jazykové zkoušce. Dále se zakazuje osobní přítomnost dětí a studentů na "zájmovém vzdělávání" ve volnočasových střediscích, školních klubech a družinách. Na všech školách kromě mateřských se také zakazuje zpěv a sportovní činnosti jako součást vzdělávání.', '<ul>
