INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (598, 'Datové schánky budou bezplatné po dobu nouzového stavu', 'Vláda projednala a přijala i několik nových opatření. Rozhodla o tom, že po dobu nouzového stavu nebude provozovateli informačního systému datových schránek náležet odměna vyplývající ze zákona o elektronických úkonech a autorizované konverzi dokumentů, tedy že používání datových schránek bude dočasně zdarma.', null, 'info', 'modal-md', 'fa fa-usd', 15, null, 153);
INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (599, 'Zákaz rekreačního ubytování', 'Zakazuje se poskytování ubytovacích služeb (třeba hotelů), kromě zmíněných výjimek.', 'Ubytovací služby lze využívat:
