INSERT INTO public.part (id, name, content, exceptions, type, modal_size, icon, cathegory_id, external_contents_id, opatreni) VALUES (609, 'Zákaz zbytečného pohybu přes den (05:00-22:59)', 'Zakazuje se zbytečný volný pohyb osob přes den (mezi 05:00 a 22:59). To znamená, že je zakázáno všechno kromě následujících výjimek...', 'Přes den jsou povoleny pouze:

