INSERT INTO public.pes_general (degree, min_value, max_value, description, color, external_contents_id) VALUES (1, 0, 20, 'Stav opatrnosti: epidemie je pod kontrolou, počet nakažených v celé populaci je nízký,
epidemie výrazně neroste, testování a trasování kontaktů je efektivní, nízké riziko komunitního
šíření nákazy', '#00cd61', 162);
INSERT INTO public.pes_general (degree, min_value, max_value, description, color, external_contents_id) VALUES (2, 21, 40, 'Stav pozornosti: objevují se lokální ohniska onemocnění, která vyžadují bezprostřední
protiepidemickou intervenci s ochranou ohrožených skupin, vysoký důraz na maximální
efektivitu testování a trasování kontaktů', '#f1d302', 162);
INSERT INTO public.pes_general (degree, min_value, max_value, description, color, external_contents_id) VALUES (3, 41, 60, 'Naléhavý stav: šíření epidemie sílí, tlak na systém zdravotní péče je zvýšený, situace
vyžaduje intenzivní sledování počtu nakažených a hospitalizovaných, vysoký důraz na
maximální efektivitu testování a trasování kontaktů, vysoké riziko komunitního šíření nákazy', '#f75a1c', 162);
INSERT INTO public.pes_general (degree, min_value, max_value, description, color, external_contents_id) VALUES (4, 61, 75, 'Vážný stav: počet nakažených v populaci je vysoký, je významné bezprostřední riziko dalšího
zhoršování situace, trasování kontaktů je omezeno, probíhá komunitní šíření nákazy', '#d31145', 162);
INSERT INTO public.pes_general (degree, min_value, max_value, description, color, external_contents_id) VALUES (5, 75, 1000, 'Kritický stav: celková kapacita systému nemocniční lůžkové a intenzivní péče se začíná blížit
svému limitu, počet nakažených v populaci je vysoký, a to včetně zásahu zranitelných skupin
obyvatel, trasování kontaktů je významně omezeno, probíhá komunitní šíření nákazy', '#401089', 162);